        countryList.add(new Country("Morocco","Rabat","https://upload.wikimedia.org/wikipedia/commons/2/2c/Flag_of_Morocco.svg.png"));
        countryList.add(new Country("Mali","Bamako","https://upload.wikimedia.org/wikipedia/commons/9/92/Flag_of_Mali.svg.png"));
        countryList.add(new Country("Ethiopia","Addis-Ababa","https://upload.wikimedia.org/wikipedia/commons/7/71/Flag_of_Ethiopia.svg.png"));
        countryList.add(new Country("Tunis","Tunis","https://upload.wikimedia.org/wikipedia/commons/c/ce/Flag_of_Tunisia.svg.png"));
        countryList.add(new Country("Madagascar","Antananarivo","https://upload.wikimedia.org/wikipedia/commons/b/bc/Flag_of_Madagascar.svg.png"));
